import React, { useState } from 'react';
import { useDropzone } from 'react-dropzone';

const Dropzone = ({ onDrop }) => {
  const [thumbnails, setThumbnails] = useState([]);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop: (acceptedFiles) => {
      // Filter acceptedFiles to only include video files (e.g., mp4, avi, mov, mkv, flv, wmv, webm)
      const acceptedVideos = acceptedFiles.filter((file) =>
        file.name.match(/\.(mp4|avi|mov|mkv|flv|wmv|webm)$/i)
      );

      // Create thumbnails for each video and store them in the state
      const videoThumbnails = acceptedVideos.map((file) => {
        const thumbnailURL = URL.createObjectURL(file);
        return thumbnailURL;
      });

      setThumbnails(videoThumbnails);

      // Execute the provided onDrop callback
      onDrop(acceptedVideos);
    },
    accept: 'video/*',
  });

  const dropzoneClasses = `border-dashed border-2 border-gray-300 rounded-md p-4 text-center ${
    isDragActive ? 'bg-gray-100' : 'bg-gray-200'
  }`;

  return (
    <div className={`h-screen w-1/4 ${dropzoneClasses}`} {...getRootProps()}>
      <input className="hidden" {...getInputProps()} />
      {isDragActive ? (
        <p className="text-gray-600">Release to drop the files here</p>
      ) : (
        <div>
          <p className="text-gray-600">
            Drag 'n' drop video files here, or click to select video files
          </p>
          <p className="text-gray-500">Accepted file types: video/*</p>
        </div>
      )}

      {thumbnails.length > 0 && (
        <div className="mt-4 grid gap-4 grid-cols-2">
          {thumbnails.map((thumbnail, index) => (
            <div key={index}>
              <img src={thumbnail} alt={`Thumbnail ${index}`} className="max-w-full h-auto" />
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Dropzone;
