import React, { useState } from 'react';

function UserInput() {
  const [formData, setFormData] = useState({
    phase: 'train',
    config_file: '',
    config_spec: 'configs/configspec.conf',
    vid_dir: '',
    frame_ext: 'png',
    out_dir: '',
    amplification_factor: 5,
    velocity_mag: false,
    fl: '',
    fh: '',
    fs: '',
    n_filter_tap: '',
    filter_type: 'Butter',
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const newValue = type === 'checkbox' ? checked : value;

    setFormData({
      ...formData,
      [name]: newValue,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission, you can access formData here.
    // You can send this data to your backend or perform any necessary actions.
  };

  return (
    <div className="bg-white p-4 shadow-md rounded-md w-1/4 fixed right-4 top-4">
      <h2 className="text-lg font-semibold">User Input</h2>
      <form onSubmit={handleSubmit}>
        {/* Input fields */}
        {/* ... (same as previous code) */}
        
        <div className="mt-4">
          <button
            type="submit"
            className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600"
          >
            Submit
          </button>
        </div>
      </form>
    </div>
  );
}

export default UserInput;
