import React from 'react';

const DisplayVideo = ({ selectedVideo }) => {
  return (
    <div className="p-4 bg-gray-200 h-5/6 p-10">
      <h2 className="text-lg font-semibold">Selected Video</h2>
      {selectedVideo ? (
        <video
          controls
          src={selectedVideo}
          className="max-w-full h-auto"
        ></video>
      ) : (
        <p>No video selected</p>
      )}
    </div>
  );
};

export default DisplayVideo;
