import React, { useState } from 'react';

function UserInput() {
  const [formData, setFormData] = useState({
    phase: 'train',
    config_file: '',
    config_spec: 'configs/configspec.conf',
    vid_dir: '',
    frame_ext: 'png',
    out_dir: '',
    amplification_factor: 5,
    velocity_mag: false,
    fl: '',
    fh: '',
    fs: '',
    n_filter_tap: '',
    filter_type: 'Butter',
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const newValue = type === 'checkbox' ? checked : value;

    setFormData({
      ...formData,
      [name]: newValue,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission here
    console.log(formData); // Replace with your submission logic
  };

  return (
    <div className="bg-white p-4 shadow-md rounded-md w-1/4 fixed right-4 top-4">
      <h2 className="text-lg font-semibold">User Input</h2>
      <form onSubmit={handleSubmit}>
        <div className="mt-4">
          <label className="block text-sm font-medium text-gray-700">Phase:</label>
          <select
            name="phase"
            value={formData.phase}
            onChange={handleChange}
            className="block w-full mt-1 p-2 border border-gray-300 rounded-md"
          >
            <option value="train">Train</option>
            <option value="test">Test</option>
            <option value="run">Run</option>
            <option value="interactive">Interactive</option>
          </select>
        </div>
        
        <div className="mt-4">
          <label className="block text-sm font-medium text-gray-700">Config File:</label>
          <input
            type="text"
            name="config_file"
            value={formData.config_file}
            onChange={handleChange}
            className="block w-full mt-1 p-2 border border-gray-300 rounded-md"
            required
          />
        </div>
        
        {/* Add similar input fields for other parameters */}
        
        <div className="mt-4">
          <button
            type="submit"
            className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600"
          >
            Submit
          </button>
        </div>
      </form>
    </div>
  );
}

export default UserInput;
