import React, { useState, useCallback } from 'react';
import { useDropzone } from 'react-dropzone';

const Dropzone = ({ onDrop }) => {
  const [thumbnails, setThumbnails] = useState([]);

  const generateThumbnail = (file) => {
    const reader = new FileReader();
    reader.onload = () => {
      setThumbnails((prevThumbnails) => [...prevThumbnails, reader.result]);
    };
    reader.readAsDataURL(file);
  };

  const onDropCallback = useCallback(
    (acceptedFiles) => {
      const videoFiles = acceptedFiles.filter((file) =>
        file.type.startsWith('video/')
      );

      setThumbnails([]); // Clear previous thumbnails
      videoFiles.forEach(generateThumbnail);

      onDrop(videoFiles);
    },
    [onDrop]
  );

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop: onDropCallback,
    accept: 'video/*',
  });

  const dropzoneClasses = `border-dashed border-2 border-gray-300 rounded-md p-4 text-center ${
    isDragActive ? 'bg-gray-100' : 'bg-gray-200'
  }`;

  return (
    <div className={`h-screen w-1/4 ${dropzoneClasses}`} {...getRootProps()}>
      <input className="hidden" {...getInputProps()} />
      {isDragActive ? (
        <p className="text-gray-600">Release to drop the files here</p>
      ) : (
        <div>
          <p className="text-gray-600">
            Drag 'n' drop video files here, or click to select video files
          </p>
          <p className="text-gray-500">Accepted file types: video/*</p>
        </div>
      )}

      {thumbnails.length > 0 && (
        <div className="mt-4 grid gap-4 grid-cols-2">
          {thumbnails.map((thumbnail, index) => (
            <div key={index}>
              <video controls src={thumbnail} className="max-w-full h-auto"></video>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Dropzone;
