import React, { useState, useEffect } from 'react';

const VideoPreview = () => {
  const [videos, setVideos] = useState([
    '1',
    '2',
    '3',
    'frontend\src\vid.mp4', // Use a relative path to vid.mp4
  ]);
  const [selectedVideo, setSelectedVideo] = useState(null);

  useEffect(() => {
    // Fetch videos from your AWS backend here
    // Replace 'your-api-endpoint' with the actual endpoint to fetch videos

    fetch('your-api-endpoint')
      .then((response) => response.json())
      .then((data) => {
        setVideos([...videos, ...data]); // Assuming data is an array of video URLs
      })
      .catch((error) => {
        console.error('Error fetching videos:', error);
      });
  }, []);

  const handleVideoClick = (video) => {
    setSelectedVideo(video === selectedVideo ? null : video);
  };

  return (
    <div className="h-4/6 mt-4 rounded-md bg-gray-200 overflow-y-auto">
      <div className="grid gap-4 grid-cols-1 p-4">
        {videos.map((video, index) => (
          <div
            key={index}
            className={`p-2 bg-white rounded-md shadow-md transition-transform transform hover:scale-105 hover:shadow-lg ${
              video === selectedVideo ? 'border border-blue-500' : ''
            }`}
            onClick={() => handleVideoClick(video)}
          >
            {video.endsWith('.mp4') ? (
              <video
                controls
                src={video}
                className="max-w-full h-auto"
                style={{ outline: 'none' }}
              ></video>
            ) : (
              <p>{video}</p>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default VideoPreview;
