// VideoPreview.js
import React, { useState, useEffect } from 'react';

const VideoPreview = () => {
  const [videos, setVideos] = useState(["1" , "2", "3"]);
  useEffect(() => {
    // Fetch videos from your AWS backend here
    // Replace 'your-api-endpoint' with the actual endpoint to fetch videos

    fetch('your-api-endpoint')
      .then((response) => response.json())
      .then((data) => {
        setVideos(data); // Assuming data is an array of video URLs
      })
      .catch((error) => {
        console.error('Error fetching videos:', error);
      });
  }, []);

  return (
    <div className="grid gap-4 grid-cols-2 p-4 h-4/6 mt-4 rounded-md bg-gray-200">
      {videos.map((video, index) => (
        <div
          key={index}
          className="p-2 bg-white h-1/6 rounded-md shadow-md transition-transform transform hover:scale-105 hover:shadow-lg"
        >
          <video controls src={video} className="max-w-full h-auto"></video>
        </div>
      ))}
    </div>
  );
};

export default VideoPreview;
