import React from 'react';

const DisplayVideo = ({ selectedVideo }) => {
  if (!selectedVideo) {
    return null; // Don't render anything if no video is selected
  }

  return (
    <div className="p-4 bg-gray-200">
      <h2 className="text-lg font-semibold">Selected Video</h2>
      <video controls src={selectedVideo} className="max-w-full h-auto"></video>
    </div>
  );
};

export default DisplayVideo;
